import styles from "./index.module.css";
import logoImg from "../../images/logo.png";
import userImg from "../../images/user.png";
import avatarImg from "../../images/avatar.png";

import React, { useState, useEffect } from "react";

export default function Header() {
  const [scroll, setScroll] = useState(false);
  const [opacity, setOpacity] = useState(100)

  useEffect(() => {
    const scroll = () => {
      let y = document.documentElement.scrollTop || document.body.scrollTop;
      if (y > 140) {
        setScroll(true);
      } else {
        setScroll(false);
      }
    };
    document.addEventListener("scroll", scroll);
  });

  return (
    <div className={styles.headerWrap}>
      {scroll && (
        <div>
          <div className={styles.fixHeader}>
            <div className={styles.tabStart}>RESUME</div>
            <div className={styles.tab}>POSTS</div>
            <div className={styles.tab}>DEMOS</div>
          </div>
          <hr className={styles.line1} />
        </div>
      )}
      <div>
        <div className={styles.bg}>
          {!scroll && <img src={avatarImg} alt="" className={styles.avatar} style={{opacity:opacity}} />}
          {!scroll && <div className="row bold">
            <div>RESUME</div>
            <div>POSTS</div>
            <div>DEMOS</div>
          </div>}
        </div>
        <hr className={styles.line2} />
      </div>

      <div className={styles.fixed}>
        <img src={logoImg} alt="" className={styles.logo} />
        <img src={userImg} alt="" className={styles.user} />
      </div>
    </div>
  );
}
